package InterviewOne;

public class SumOfNoInArry {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		       int ab[] = new int [] {2,3,4,5,6};
		       
		       int sum =0;
		       for(int i =0; i<ab.length;i++) {
		    	   
		    	  sum = sum+ab[i];
		       }
		       
		       System.out.println("the sum of total number in array is ="+sum);
	}

}
