package MethodWriting;

public class StartPatteran2 {

	/*
	***
	*****
	*******
	**********/
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int k=1;
		for(int i=1; i<=5;i++) {
			
			for(int j=1;j<=k;j++) {
				System.out.print("*");
			}
			k=k+2;
			System.out.println();
		}

	}

}
